CREATE TABLE transaction IF NOT EXIST (    id SERIAL PRIMARY KEY,    account_id INTEGER,    amount DOUBLE,    device_id INTEGER,    label VARCHAR(255),    date DATE,    hour TIMESTAMP,    note VARCHAR(255),    paiement_method_id INTEGER,    paiement_status_id INTEGER,    location VARCHAR(255));--ADD CONSTRAINT fk_account FOREIGN KEY (account_id) REFERENCES account(id);--ADD CONSTRAINT fk_device FOREIGN KEY (device_id) REFERENCES device(id);--ADD CONSTRAINT fk_paiement_method FOREIGN KEY (paiement_method_id) REFERENCES paiement_method(id);--ADD CONSTRAINT fk_paiement_status FOREIGN KEY (paiement_status_id) REFERENCES paiement_status(id);